import React, { useState } from 'react';

const ProductGrid: React.FC = () => {
  // Dummy product data for demonstration
  const products = [
    { id: 1, name: 'Product 1', price: 29.99, imageUrl: 'https://via.placeholder.com/300', sizes: ['S', 'M', 'L', 'XL'] },
    { id: 2, name: 'Product 2', price: 39.99, imageUrl: 'https://via.placeholder.com/300', sizes: ['S', 'M', 'L', 'XL'] },
    { id: 3, name: 'Product 3', price: 49.99, imageUrl: 'https://via.placeholder.com/300', sizes: ['S', 'M', 'L', 'XL'] },
    { id: 4, name: 'Product 4', price: 59.99, imageUrl: 'https://via.placeholder.com/300', sizes: ['S', 'M', 'L'] },
    { id: 5, name: 'Product 1', price: 29.99, imageUrl: 'https://via.placeholder.com/300', sizes: ['S', 'M', 'L'] },
    { id: 6, name: 'Product 2', price: 39.99, imageUrl: 'https://via.placeholder.com/300', sizes: ['S', 'M', 'L'] },
    { id: 7, name: 'Product 3', price: 49.99, imageUrl: 'https://via.placeholder.com/300', sizes: ['S', 'M', 'L'] },
    { id: 8, name: 'Product 4', price: 59.99, imageUrl: 'https://via.placeholder.com/300', sizes: ['S', 'M', 'L'] },
  ];

  const [hoveredProductId, setHoveredProductId] = useState<number | null>(null);

  return (
    <section className="container mx-auto px-6 py-12">
      <h2 className="text-3xl font-semibold mb-8">Popular Products</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {products.map((product) => (
          <div
            key={product.id}
            className="relative bg-white rounded-lg shadow-lg overflow-hidden"
            onMouseEnter={() => setHoveredProductId(product.id)}
            onMouseLeave={() => setHoveredProductId(null)}
          >
            <img src={product.imageUrl} alt={product.name} className="h-64 w-full object-cover" />
            {hoveredProductId === product.id && (
              <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center">
                <div className="text-white">
                  <p className="mb-2">Available Sizes:</p>
                  <div className="flex space-x-2">
                    {product.sizes.map((size) => (
                      <span key={size} className="bg-gray-800 py-1 px-2 rounded">
                        {size}
                      </span>
                    ))}
                  </div>
                </div>
              </div>
            )}
            <div className="p-4">
              <h3 className="text-xl font-semibold mb-2">{product.name}</h3>
              <p className="text-gray-700">${product.price}</p>
              <button className="mt-4 bg-gray-900 text-white py-2 px-4 rounded-full hover:bg-gray-800 focus:outline-none focus:bg-gray-800">
                Add to Cart
              </button>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default ProductGrid;
